import React from 'react';
import {CardList}  from '../components/card-list/index.jsx';
import { FilterBox } from '../components/filter-box/index.jsx';
 
import './index.css';

export const Ecommerce = (props) => {
  const products = props.products 
  const [selectedOption, setSelectedOption] = React.useState('All');
  const [searchTerm, setSearchTerm] = React.useState("");
  const [searchResults, setSearchResults] = React.useState([])

  function getAvailableFilterOptions(products){
    var availableOptions = [];

    products.forEach(product => {
      availableOptions.indexOf(product.type) === -1 && availableOptions.push(product.type);
    })
    return availableOptions;
  };
  
  function getAvailableSearchOptions(products){
    var availableOptions = [];

    products.forEach(product => {
      availableOptions.indexOf(product.productName) === -1 && availableOptions.push(product.productName);
    })
    return availableOptions;
  };



  function getFilteredProducts(products) {
    if(selectedOption === 'All')
      return products;

    return products.filter(p => p.type === selectedOption);
  };

    const searchHandler = (searchTerm) => {
        setSearchTerm(searchTerm);
        if(searchTerm !== ""){
            const newProductList = products.filter((product) => {
                return(Object.values(product).join(" ").toLowerCase().includes(searchTerm.toLowerCase()))
            })
            setSearchResults(newProductList)
        }
        else{
            setSearchResults(products)
        }
    } 


  

  return (
    <div className="App">
     
      <FilterBox term = {searchTerm} searchKeyword = {searchHandler} filterOptions={getAvailableFilterOptions(products)} cb={setSelectedOption} />
      <CardList products={getFilteredProducts(products)}/>
      
    </div>
  )
}
