      <SearchBox searchOptions= {getSearchProducts(products)} cb={setSelectedOption} />
      <FilterBox term = {searchTerm} searchKeyword = {searchHandler} filterOptions={getAvailbleFilterOptions(products)} cb={setSelectedOption} />
      <CardList products={getFilteredProducts(products)}/>
      
    </div>
  )
}
